mapper.insert:
merge into JX_OUTSOURCE_INSURANCE a using (
SELECT #{item.jxInsuranceId,jdbcType=DECIMAL} as jxInsuranceId, #{item.userName,jdbcType=VARCHAR} as userName, #{item.loginName,jdbcType=VARCHAR} as loginName, #{item.identityCard,jdbcType=VARCHAR} as identityCard, #{item.selYear,jdbcType=VARCHAR} as selYear, #{item.period,jdbcType=VARCHAR} as period, #{item.companyId,jdbcType=VARCHAR} as companyId, #{item.companyName,jdbcType=VARCHAR} as companyName, #{item.outsourcePay,jdbcType=DECIMAL} as outsourcePay, #{item.wbPostSalary,jdbcType=DECIMAL} as wbPostSalary, #{item.wbPerformancePay,jdbcType=DECIMAL} as wbPerformancePay, #{item.benefits,jdbcType=DECIMAL} as benefits, #{item.otherIncome,jdbcType=DECIMAL} as otherIncome, #{item.wbOtherCostGr,jdbcType=DECIMAL} as wbOtherCostGr, #{item.wbOtherCostGs,jdbcType=DECIMAL} as wbOtherCostGs, #{item.wbBasicOld,jdbcType=DECIMAL} as wbBasicOld, #{item.wbBasicMedical,jdbcType=DECIMAL} as wbBasicMedical, #{item.wbLoseWork,jdbcType=DECIMAL} as wbLoseWork, #{item.wbInjury,jdbcType=DECIMAL} as wbInjury, #{item.wbBirth,jdbcType=DECIMAL} as wbBirth, #{item.wbFund,jdbcType=DECIMAL} as wbFund, #{item.otherRisks,jdbcType=DECIMAL} as otherRisks FROM dual
) T on (a.JX_INSURANCE_ID = T.jxInsuranceId) WHEN matched THEN UPDATE SET a.LOGIN_NAME = T.loginName, a.USER_NAME = T.userName, a.IDENTITY_CARD = T.identityCard, a.SEL_YEAR = T.selYear, a.PERIOD = T.period, a.COMPANY_ID = T.companyId, a.COMPANY_NAME = T.companyName, a.OUTSOURCE_PAY = T.outsourcePay, a.WB_POST_SALARY = T.wbPostSalary, a.WB_PERFORMANCE_PAY = T.wbPerformancePay, a.BENEFITS = T.benefits, a.OTHER_INCOME = T.otherIncome, a.WB_OTHER_COST_GR = T.wbOtherCostGr, a.WB_OTHER_COST_GS = T.wbOtherCostGs, a.WB_BASIC_OLD = T.wbBasicOld, a.WB_BASIC_MEDICAL = T.wbBasicMedical, a.WB_LOSE_WORK = T.wbLoseWork, a.WB_INJURY = T.wbInjury, a.WB_BIRTH = T.wbBirth, a.WB_FUND = T.wbFund, a.OTHER_RISKS = T.otherRisks WHEN not matched THEN insert(JX_INSURANCE_ID,USER_NAME, LOGIN_NAME, IDENTITY_CARD, SEL_YEAR, PERIOD, COMPANY_ID, COMPANY_NAME, OUTSOURCE_PAY, WB_POST_SALARY, WB_PERFORMANCE_PAY, BENEFITS, OTHER_INCOME, WB_OTHER_COST_GR, WB_OTHER_COST_GS, WB_BASIC_OLD, WB_BASIC_MEDICAL, WB_LOSE_WORK, WB_INJURY, WB_BIRTH, WB_FUND, OTHER_RISKS ) VALUES(T.jxInsuranceId, T.userName, T.loginName, T.identityCard, T.selYear, T.period, T.companyId, T.companyName, T.outsourcePay, T.wbPostSalary, T.wbPerformancePay, T.benefits, T.otherIncome, T.wbOtherCostGr, T.wbOtherCostGs, T.wbBasicOld, T.wbBasicMedical, T.wbLoseWork, T.wbInjury, T.wbBirth, T.wbFund, T.otherRisks )
