mapper.insert:
merge into OUTSTAFF_PAY_YEARENDAWARD_ZB a using (
SELECT #{item.payYearId,jdbcType=DECIMAL} as payYearId, #{item.loginName,jdbcType=VARCHAR} as loginName, #{item.userName,jdbcType=VARCHAR} as userName, #{item.identityCard,jdbcType=VARCHAR} as identityCard, #{item.sex,jdbcType=VARCHAR} as sex, #{item.jobRank,jdbcType=VARCHAR} as jobRank, #{item.jobWagesRank,jdbcType=VARCHAR} as jobWagesRank, #{item.postSequence,jdbcType=VARCHAR} as postSequence, #{item.injury,jdbcType=VARCHAR} as injury, #{item.annualBonus,jdbcType=DECIMAL} as annualBonus, #{item.emphasisZxReward,jdbcType=DECIMAL} as emphasisZxReward, #{item.superHarvestReward,jdbcType=DECIMAL} as superHarvestReward, #{item.yearZxRew,jdbcType=DECIMAL} as yearZxRew, #{item.yearHonourRew,jdbcType=DECIMAL} as yearHonourRew, #{item.yearOther,jdbcType=DECIMAL} as yearOther, #{item.attendanceDeduction,jdbcType=DECIMAL} as attendanceDeduction, #{item.totalDue,jdbcType=DECIMAL} as totalDue, #{item.remark,jdbcType=VARCHAR} as remark, #{item.thisButtonNumb,jdbcType=DECIMAL} as thisButtonNumb, #{item.thisButtonAmount,jdbcType=DECIMAL} as thisButtonAmount, #{item.actualHairSum,jdbcType=DECIMAL} as actualHairSum FROM dual
) T on (a.PAY_YEAR_ID = T.payYearId) WHEN matched THEN UPDATE SET a.LOGIN_NAME = T.loginName, a.USER_NAME = T.userName, a.IDENTITY_CARD = T.identityCard, a.SEX = T.sex, a.JOB_RANK = T.jobRank, a.JOB_WAGES_RANK = T.jobWagesRank, a.POST_SEQUENCE = T.postSequence, a.INJURY = T.injury, a.ANNUAL_BONUS = T.annualBonus, a.EMPHASIS_ZX_REWARD = T.emphasisZxReward, a.SUPER_HARVEST_REWARD = T.superHarvestReward, a.YEAR_ZX_REW = T.yearZxRew, a.YEAR_HONOUR_REW = T.yearHonourRew, a.YEAR_OTHER = T.yearOther, a.ATTENDANCE_DEDUCTION = T.attendanceDeduction, a.TOTAL_DUE = T.totalDue, a.REMARK = T.remark, a.THIS_BUTTON_NUMB = T.thisButtonNumb, a.THIS_BUTTON_AMOUNT = T.thisButtonAmount, a.ACTUAL_HAIR_SUM = T.actualHairSum WHEN not matched THEN INSERT(PAY_YEAR_ID, LOGIN_NAME, USER_NAME,IDENTITY_CARD, SEX, JOB_RANK, JOB_WAGES_RANK, POST_SEQUENCE, INJURY,ANNUAL_BONUS, EMPHASIS_ZX_REWARD, SUPER_HARVEST_REWARD, YEAR_ZX_REW, YEAR_HONOUR_REW, YEAR_OTHER,ATTENDANCE_DEDUCTION, TOTAL_DUE, REMARK, THIS_BUTTON_NUMB, THIS_BUTTON_AMOUNT, ACTUAL_HAIR_SUM) VALUES(T.payYearId,T.loginName,T.userName,T.identityCard,T.sex,T.jobRank,T.jobWagesRank, T.postSequence,T.injury,T.annualBonus,T.emphasisZxReward,T.superHarvestReward,T.yearZxRew, T.yearHonourRew,T.yearOther,T.attendanceDeduction,T.totalDue,T.remark,T.thisButtonNumb, T.thisButtonAmount,T.actualHairSum)
