WHERE ATI_BASE_FORM_ID = #{atiBaseFormId}
AND PARAMETER = 'HR_TEXT'

UPDATE ATI_SPECIFIC_FORM
SET PARAM_VALUE = #{paramValue}
WHERE ATI_BASE_FORM_ID = #{atiBaseFormId}
